document.addEventListener("DOMContentLoaded",function(){const n=document.querySelector(".hamburger"),s=document.querySelector(".nav-links"),o=document.getElementById("theme-toggle"),t=document.getElementById("language-select");n.addEventListener("click",function(){s.classList.toggle("active")});let e=!0;document.body.classList.add("dark-theme"),o.addEventListener("click",function(){e=!e,document.body.classList.toggle("dark-theme",e)}),t.addEventListener("change",function(){const e=t.value;alert("Selected language: "+e)})})